// src/pages/HrUpload.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import {
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Typography,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import AddProblemSlide from "../components/AddProblemSlide";

function HrUpload({ onEditProblem }) {
  const [questions, setQuestions] = useState([]);
  const [difficultyFilter, setDifficultyFilter] = useState("");
  const [isSlideOpen, setIsSlideOpen] = useState(false);
  const [openPreview, setOpenPreview] = useState(false);
  const [previewData, setPreviewData] = useState({ easy: [], medium: [], hard: [] });
  const navigate = useNavigate();

  useEffect(() => {
    fetchQuestions();
  }, []);

  const fetchQuestions = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/questions/");
      setQuestions(response.data.problems);
    } catch (error) {
      console.error("Error fetching questions:", error);
    }
  };

  const handlePreview = () => {
    // Filter questions into difficulty levels for the preview dialog
    setPreviewData({
      easy: questions.filter((question) => question.level === "easy"),
      medium: questions.filter((question) => question.level === "medium"),
      hard: questions.filter((question) => question.level === "hard"),
    });
    setOpenPreview(true);
  };

  const handleClosePreview = () => {
    setOpenPreview(false);
  };

  const handleModify = (problem) => {
    if (onEditProblem) onEditProblem(problem); // Trigger the edit function passed from the parent component
    setOpenPreview(false); // Close the preview dialog
    navigate("/ManualSelectUI"); // Navigate to the edit page
  };

  const handleDeleteQuestion = async (questionId) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/questions/${questionId}/`);
      fetchQuestions(); // Refresh questions after deletion
    } catch (error) {
      console.error("Error deleting question:", error);
    }
  };

  const filteredQuestions = questions.filter((q) =>
    difficultyFilter ? q.level === difficultyFilter : true
  );

  return (
    <div style={{ padding: "20px", maxWidth: "800px", margin: "auto" }}>
      <h1>Questions</h1>
      <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "10px" }}>
        <span>Count: {filteredQuestions.length}</span>
        <button
          onClick={() => setIsSlideOpen(true)}
          style={{
            background: "blue",
            color: "white",
            borderRadius: "5px",
            padding: "5px 10px",
          }}
        >
          + Add Question
        </button>
      </div>

      <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "20px" }}>
        <select
          onChange={(e) => setDifficultyFilter(e.target.value)}
          value={difficultyFilter}
          style={{ padding: "5px" }}
        >
          <option value="">Filter by difficulty</option>
          <option value="easy">Easy</option>
          <option value="medium">Medium</option>
          <option value="hard">Hard</option>
        </select>
        <button
          onClick={handlePreview}
          style={{
            background: "gray",
            color: "white",
            borderRadius: "5px",
            padding: "5px 10px",
          }}
        >
          Preview
        </button>
      </div>

      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr style={{ backgroundColor: "#f2f2f2" }}>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>S.No</th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Title</th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Difficulty</th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredQuestions.map((question, index) => (
            <tr key={question.id}>
              <td style={{ border: "1px solid #ddd", padding: "8px", textAlign: "center" }}>{index + 1}</td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>{question.title}</td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>{question.level}</td>
              <td style={{ border: "1px solid #ddd", padding: "8px", textAlign: "center" }}>
                <button
                  onClick={() => handleModify(question)}
                  style={{
                    background: "Green",
                    color: "white",
                    borderRadius: "5px",
                    padding: "5px 10px",
                    marginRight: "5px",
                  }}
                >
                  Modify
                </button>
                <button
                  onClick={() => handleDeleteQuestion(question.id)}
                  style={{
                    background: "red",
                    color: "white",
                    borderRadius: "5px",
                    padding: "5px 10px",
                  }}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Preview Dialog */}
      <Dialog open={openPreview} onClose={handleClosePreview} fullWidth maxWidth="md">
        <DialogTitle>Preview Problems by Difficulty</DialogTitle>
        <DialogContent>
          {["easy", "medium", "hard"].map((level) => (
            <Accordion key={level} disabled={!previewData[level].length}>
              <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                <Typography variant="h6" sx={{ textTransform: "capitalize" }}>
                  {level} ({previewData[level].length})
                </Typography>
              </AccordionSummary>
              <AccordionDetails>
                {previewData[level].map((problem) => (
                  <div key={problem.id} style={{ marginBottom: "15px" }}>
                    <Typography variant="h6">{problem.title}</Typography>
                    <Typography variant="body1">{problem.problem_statement}</Typography>
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={() => handleModify(problem)}
                      style={{ marginRight: "10px", marginTop: "10px" }}
                    >
                      Modify
                    </Button>
                    <Button
                      variant="contained"
                      color="error"
                      onClick={() => handleDeleteQuestion(problem.id)}
                      style={{ marginTop: "10px" }}
                    >
                      Delete
                    </Button>
                  </div>
                ))}
              </AccordionDetails>
            </Accordion>
          ))}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClosePreview} color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>

      <AddProblemSlide isOpen={isSlideOpen} onClose={() => setIsSlideOpen(false)} />
    </div>
  );
}

export default HrUpload;
