// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import CreateContest from './pages/CreateContest';
import ContestPage from './pages/ContestPage';
import AutoContest from './pages/AutoContest';
import SelectTestOption from './pages/SelectTestOption';
import FileUpload from './pages/FileUpload';
import ManualSelectUI from './pages/ManualSelectUI';
import HrUpload from './pages/hrUploadPage';

function App() {
  const [selectedProblem, setSelectedProblem] = useState(null); // State for storing the problem to edit

  const navigate = useNavigate();

  // Function to handle edit request from HrUploadPage
  const handleEditProblem = (problem) => {
    setSelectedProblem(problem); // Set the problem to be edited
    navigate('/ManualSelectUI'); // Navigate to ManualSelectUI for editing
  };

  // Function to handle save and navigate back to HrUploadPage
  const handleSaveProblem = (updatedProblem) => {
    setSelectedProblem(null); // Clear the selected problem after saving
    navigate('/TestUpload'); // Navigate back to HrUploadPage
  };

  return (
    <Router>
      <Routes>
        {/* Main routes */}
        <Route path="/" element={<CreateContest />} />
        <Route path="/SelectTestOption" element={<SelectTestOption />} />
        <Route path="/FileUpload" element={<FileUpload />} />

        {/* Pass onEditProblem to HrUpload for modifying problems */}
        <Route path="/TestUpload" element={<HrUpload onEditProblem={handleEditProblem} />} />

        {/* Pass selectedProblem and onSave to ManualSelectUI for editing */}
        <Route path="/ManualSelectUI" element={<ManualSelectUI problemData={selectedProblem} onSave={handleSaveProblem} />} />

        {/* User pages */}
        <Route path="/AutoContest" element={<AutoContest />} />
        <Route path="/contest" element={<ContestPage />} />
      </Routes>
    </Router>
  );
}

export default App;
