import React, { useState, useEffect } from 'react';
import { Button, Typography, Box, Dialog, DialogActions, DialogContent, DialogTitle } from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const HrUpload = () => {
  const [questions, setQuestions] = useState([]);
  const [deleteConfirm, setDeleteConfirm] = useState(false);
  const [selectedQuestionId, setSelectedQuestionId] = useState(null);
  const navigate = useNavigate();

  // Fetch questions from tempQuestions
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.get('http://localhost:8000/manualProblems/');
        setQuestions(response.data.problems);
      } catch (error) {
        console.error('Failed to fetch questions:', error);
      }
    };
    fetchQuestions();
  }, []);

  const handleModify = (question) => {
    navigate('/manualSelectUI', { state: { question } });
  };

  const handleDelete = async (questionId) => {
    try {
      await axios.delete('http://localhost:8000/manualProblems/', { data: { id: questionId } });
      setQuestions(questions.filter(q => q.id !== questionId));
      setDeleteConfirm(false);
    } catch (error) {
      console.error("Failed to delete question:", error);
    }
  };

  return (
    <Box p={3}>
      <Typography variant="h4" align="center" gutterBottom>
        HR Upload
      </Typography>
      {questions.map((question) => (
        <Box key={question.id} mb={3} p={2} border="1px solid #ddd" borderRadius={1}>
          <Typography variant="h6">{question.title}</Typography>
          <Typography variant="body1">{question.problem_statement}</Typography>
          <Button variant="contained" color="primary" onClick={() => handleModify(question)}>
            Modify
          </Button>
          <Button variant="contained" color="error" onClick={() => { setSelectedQuestionId(question.id); setDeleteConfirm(true); }}>
            Delete
          </Button>
        </Box>
      ))}
      <Dialog open={deleteConfirm} onClose={() => setDeleteConfirm(false)}>
        <DialogTitle>Confirm Deletion</DialogTitle>
        <DialogContent>
          <Typography>Are you sure you want to delete this question?</Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteConfirm(false)} color="primary">Cancel</Button>
          <Button onClick={() => handleDelete(selectedQuestionId)} color="error">Delete</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default HrUpload;
