import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Box, Grid, IconButton } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

const ManualSelectUI = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [questionData, setQuestionData] = useState(location.state?.question || {});
  const [testCases, setTestCases] = useState(questionData.samples || [{ inputs: [''], output: '' }]);

  useEffect(() => {
    if (!questionData.id) navigate('/hrUpload'); // Redirect if no question data is provided
  }, [questionData, navigate]);

  const handleSave = async () => {
    try {
      const updatedQuestion = {
        ...questionData,
        samples: testCases,
      };
      await axios.put('http://localhost:8000/manualProblems/', { problems: [updatedQuestion] });
      navigate('/hrUpload');
    } catch (error) {
      console.error("Failed to save question:", error);
    }
  };

  const handleAddTestCase = () => {
    setTestCases([...testCases, { inputs: [''], output: '' }]);
  };

  const handleRemoveTestCase = (index) => {
    const updatedTestCases = [...testCases];
    updatedTestCases.splice(index, 1);
    setTestCases(updatedTestCases);
  };

  return (
    <Box p={3}>
      <Typography variant="h4" align="center" gutterBottom>
        Edit Question
      </Typography>
      <Grid container spacing={4}>
        <Grid item xs={12}>
          <TextField
            fullWidth
            label="Title"
            variant="outlined"
            margin="normal"
            value={questionData.title}
            onChange={(e) => setQuestionData({ ...questionData, title: e.target.value })}
          />
          <TextField
            fullWidth
            label="Description"
            variant="outlined"
            margin="normal"
            multiline
            rows={3}
            value={questionData.problem_statement}
            onChange={(e) => setQuestionData({ ...questionData, problem_statement: e.target.value })}
          />
        </Grid>
      </Grid>

      <Box mt={2}>
        <Typography variant="h6">Test Cases</Typography>
        {testCases.map((testCase, index) => (
          <Box key={index} mb={2}>
            <Typography variant="subtitle1">Test Case {index + 1}</Typography>
            {testCase.inputs.map((input, inputIndex) => (
              <TextField
                key={inputIndex}
                fullWidth
                label={`Input ${inputIndex + 1}`}
                variant="outlined"
                margin="normal"
                value={input}
                onChange={(e) => {
                  const updatedTestCases = [...testCases];
                  updatedTestCases[index].inputs[inputIndex] = e.target.value;
                  setTestCases(updatedTestCases);
                }}
              />
            ))}
            <TextField
              fullWidth
              label="Output"
              variant="outlined"
              margin="normal"
              value={testCase.output}
              onChange={(e) => {
                const updatedTestCases = [...testCases];
                updatedTestCases[index].output = e.target.value;
                setTestCases(updatedTestCases);
              }}
            />
            <IconButton onClick={() => handleRemoveTestCase(index)} color="error">
              <RemoveIcon />
            </IconButton>
          </Box>
        ))}
        <Button onClick={handleAddTestCase} variant="contained" color="success">
          Add Test Case
        </Button>
      </Box>

      <Box mt={4}>
        <Button variant="contained" color="primary" onClick={handleSave}>
          Save
        </Button>
      </Box>
    </Box>
  );
};

export default ManualSelectUI;
